import styles from "../styles/BaseDiscordCard.module.css";
import preflightStyles from "../styles/DiscordCardPreflight.module.css";
import BadgeSection from "./BadgeSection";
import { Badge, ConnectionStatus } from "../types";

const BaseDiscordCard = ({
  imageUrl,
  bannerUrl,
  primaryColor,
  accentColor,
  badges,
  connectionStatus = "online",
  children,
}: {
  imageUrl: string;
  bannerUrl: string;
  primaryColor: string;
  accentColor: string;
  badges?: Badge[];
  connectionStatus?: ConnectionStatus;
  children: React.JSX.Element | React.JSX.Element[];
}) => {
  return (
    <div className={`${styles.container} ${preflightStyles.discordCardScope}`}>
      <div
        className={styles.cardWrapper}
        style={{
          background: `linear-gradient(to bottom, ${primaryColor}, ${accentColor})`,
        }}
      >
        <div className={styles.cardContent}>
          <div className={styles.profileSection}>
            <img
              src={imageUrl}
              alt="Discord profile picture"
              className={styles.profileImage}
            />
            <div
              className={styles.profileBackgroundGradient}
              style={{
                background: `linear-gradient(to bottom, ${primaryColor} 60%, transparent 40%)`,
              }}
            ></div>
            <div
              className={styles.profileShadowOverlay}
              style={{
                background: `linear-gradient(to bottom, #00000099 60%, transparent 40%)`,
              }}
            />
          </div>
          <img src={bannerUrl} className={styles.bannerImage} alt="" />
          <div className={styles.statusIndicator}>
            <img
              src={`${
                connectionStatus === "offline"
                  ? "data:image/png;base64,"
                  : connectionStatus === "idle"
                  ? "data:image/png;base64,"
                  : connectionStatus === "dnd"
                  ? "data:image/png;base64,"
                  : "data:image/png;base64,"
              }`}
              className={styles.statusIcon}
              alt=""
              aria-label={`This user's status is ${connectionStatus}`}
            />
          </div>
          {badges && <BadgeSection badges={badges} />}
        </div>
        <div className={styles["discord-card-outer-body"]}>
          <div className={styles["discord-card-inner-body"]}>{children}</div>
        </div>
      </div>
    </div>
  );
};

export default BaseDiscordCard;
